	  	// 	 {headers: {"app_user_id":"test", "app_user_name": "test"}}
  		// ).then(response => {
  		// 	console.log(response.data);
  		// });

  }

  render() {
    return (
    	<Layout>
    		<h1>Create Blog</h1>
	      <form onSubmit={this.submitForm}>
	        <label>
	          Title:
	          <input type="text" value={this.state.title} onChange={(e) => {this.handleChange("title", e)}}/>
	        </label>
	        <label>
	          Content:
	          <input type="text" value={this.state.content} onChange={(e) => {this.handleChange("content", e)}}/>
	        </label>
	        <input type="submit" value="Submit" />
	      </form>
	    </Layout>
    );
  }
}

export default BlogForm;
