import Layout from '../../components/basicLayout';
import axios from 'axios';

const Blog = props => (
  <Layout>
    <h1>{props.blog.title}</h1>
    <p>{props.blog.content.replace(/<[/]?[pb]>/g, '')}</p>
  </Layout>
);

Blog.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`https://api.tvmaze.com/blogs/${id}`);
  const blog = await res.json();

  console.log(`Fetched blog: ${blog.name}`);

  return { blog };
};

export default Blog;
